frappe.ui.form.on('Employee', {
    refresh: function (frm) {
        frm.add_custom_button(__('Vertragsverhandlungen übernehmen'), () => {
            frappe.confirm(
                __(
                    "Wollen Sie wirklich ALLE Vertragsverhandlungen in die aktuell laufenden Verträge übernehmen und dadurch die Daten der Vertragsverhandlung löschen? " +
                    "Alle Änderungen werden Sie in einem Protokoll in den Kommentaren finden. Der Vorgang ist unumkehrbar!"
                ),
                () => {
                    console.log("Confirmed: Starting Vertragsverhandlungen update");
                    frappe.msgprint("Vertragsverhandlungen werden bearbeitet...");

                    let changes = [];

                    function update_field(source, target, field_name) {
                        let source_value = frm.doc[source];
                        if (source_value !== null && source_value !== undefined && source_value !== "") {
                            let original_value = frm.doc[target];

                            // Update target field
                            frm.set_value(target, source_value);

                            // Log the change
                            changes.push({
                                field_name: field_name,
                                old_value: original_value || 'leer',
                                new_value: source_value,
                                variable_name: target
                            });

                            // Clear source field
                            frm.set_value(source, null);
                        }
                    }

                    // Map für alle Felder
                    const fields_map = {
                        
                        custom_grundvertrag_in_verhandlung: [
                            { source: "custom_wochenstunden_grundvertrag_in_verhandlung", target: "custom_wochenstunden_grundvertrag", label: "Wochenstunden Grundvertrag" },
                            { source: "custom_gehalt_des_grundvertrags_in_verhandlung", target: "custom_gehalt_des_grundvertrags", label: "Gehalt des Grundvertrags" },
                            { source: "custom_grundlage_der_vergütung_tarifdetails_in_verhandlung", target: "custom_grundlage_der_vergütung", label: "Grundlage der Vergütung" },
                            { source: "custom_unterzeichnungsdatum_in_verhandlung", target: "custom_unterzeichnungsdatum", label: "Unterzeichnungsdatum" },
                            { source: "custom_start_des_grundvertrags_in_verhandlung", target: "custom_start_des_grundvertrags", label: "Start des Grundvertrags" },
                            { source: "custom_ende_des_arbeitsverhältnis_in_verhandlung", target: "custom_ende_des_arbeitsverhältnisses", label: "Ende des Arbeitsverhältnisses" },
                            { source: "custom_jahressonderzahlung_grundvertrag_in_verhandlung", target: "custom_jahressonderzahlung_grundvertrag", label: "Jahressonderzahlung Grundvertrag" },
                            { source: "custom_bonus_grundvertrag_in_verhandlung", target: "custom_bonus_grundvertrag", label: "Bonus Grundvertrag" },
                            { source: "custom_vertragslaufzeit_in_verhandlung", target: "custom_vertragslaufzeit", label: "Vertragslaufzeit" }
                        ],
                        custom_projekt_1_in_verhandlung: [
                            { source: "custom_projekt_1_iv", target: "custom_projekt_1", label: "Projekt 1" },
                            { source: "custom_name_projekt_1_iv", target: "custom_name_projekt_1", label: "Name Projekt 1" },
                            { source: "custom_tätigkeitsbezeichnung_1_iv", target: "custom_tätigkeitsbezeichnung_1", label: "Tätigkeitsbezeichnung Projekt 1" },
                            { source: "custom_wochenstunden_projekt_1_iv", target: "custom_wochenstunden_projekt_1", label: "Wochenstunden Projekt 1" },
                            { source: "custom_wochenstunden_zum_grundvertrag_hinzurechnen_iv", target: "custom_wochenstunden_zum_grundvertrag_hinzurechnen", label: "Wochenstunden zum Grundvertrag" },
                            { source: "custom_start_der_1_arbeit_iv", target: "custom_start_der_1_arbeit", label: "Start der 1. Arbeit" },
                            { source: "custom_befristung_des_1_vertrags_iv", target: "custom_befristung_des_1_vertrags", label: "Befristung des 1. Vertrags" },
                            { source: "custom_gehalt_projekt_1_iv", target: "custom_gehalt_projekt_1", label: "Gehalt Projekt 1" },
                            { source: "custom_grundlage_der_vergütung_tarifdetails_pr_1_iv", target: "custom_grundlage_der_vergütung_tarifdetails_pr_1", label: "Vergütungsdetails Projekt 1" },
                            { source: "custom_ist_zusätzliche_vergütung_zum_grundgehalt_iv", target: "custom_ist_zusätzliche_vergütung_zum_grundgehalt", label: "Zusätzliche Vergütung" },
                            { source: "custom_jahressonderzahlung_projekt_1_in_pr_iv", target: "custom_jahressonderzahlung", label: "Jahressonderzahlung Projekt 1" },
                            { source: "custom_jahressonderzahlung_projekt_1_in_e_iv", target: "custom_jahressonderzahlung_projekt_1_in_", label: "Sonderzahlung Projekt 1 in E" }
                        ],
                        custom_projekt_2_in_verhandlung: [
                            { source: "custom_projekt_2_iv", target: "custom_projekt_2", label: "Projekt 2" },
                            { source: "custom_name_projekt_2_iv", target: "custom_name_projekt_2", label: "Name Projekt 2" },
                            { source: "custom_tätigkeitsbezeichnung_2_iv", target: "custom_tätigkeitsbezeichnung_2", label: "Tätigkeitsbezeichnung Projekt 2" },
                            { source: "custom_wochenstunden_projekt_2_iv", target: "custom_wochenstunden_projekt_2", label: "Wochenstunden Projekt 2" },
                            { source: "custom_wochenstunden_zum_grundvertrag_hinzurechnen_1_iv", target: "custom_wochenstunden_zum_grundvertrag_hinzurechnen_1", label: "Wochenstunden zum Grundvertrag 2" },
                            { source: "custom_start_der_2_arbeit_iv", target: "custom_start_der_2_arbeit", label: "Start der 2. Arbeit" },
                            { source: "custom_befristung_des_2_vertrags_iv", target: "custom_befristung_des_2_vertrags", label: "Befristung des 2. Vertrags" },
                            { source: "custom_gehalt_projekt_2_iv", target: "custom_gehalt_projekt_2", label: "Gehalt Projekt 2" },
                            { source: "custom_grundlage_der_vergütung_tarifdetails_pr_2_iv", target: "custom_grundlage_der_vergütung_tarifdetails_pr_2", label: "Vergütungsdetails Projekt 2" },
                            { source: "custom_ist_zusätzliche_vergütung_zum_grundgehalt_1_iv", target: "custom_ist_zusätzliche_vergütung_zum_grundgehalt_1", label: "Zusätzliche Vergütung" },
                            { source: "custom_jahressonderzahlung_projekt_2_in_p_iv", target: "custom_jahressonderzahlung_projekt_2", label: "Jahressonderzahlung Projekt 2" },
                            { source: "custom_jahressonderzahlung_projekt_2_in_e_iv_", target: "custom_jahressonderzahlung_projekt_2_in_", label: "Sonderzahlung Projekt 2 in E" }
                            ],
                        custom_projekt_3_in_verhandlung: [
                            { source: "custom_projekt_3_iv", target: "custom_projekt_3", label: "Projekt 3" },
                            { source: "custom_name_projekt_3_iv", target: "custom_name_projekt_3", label: "Name Projekt 3" },
                            { source: "custom_tätigkeitsbezeichnung_3_iv", target: "custom_tätigkeitsbezeichnung_3", label: "Tätigkeitsbezeichnung Projekt 3" },
                            { source: "custom_wochenstunden_projekt_3_iv", target: "custom_wochenstunden_projekt_3", label: "Wochenstunden Projekt 3" },
                            { source: "custom_wochenstunden_zum_grundvertrag_hinzurechnen_2_iv", target: "custom_wochenstunden_zum_grundvertrag_hinzurechnen_2", label: "Wochenstunden zum Grundvertrag 3" },
                            { source: "custom_start_der_3_arbeit_iv", target: "custom_start_der_3_arbeit", label: "Start der 3. Arbeit" },
                            { source: "custom_befristung_des_3_vertrags_iv", target: "custom_befristung_des_3_vertrags", label: "Befristung des 3. Vertrags" },
                            { source: "custom_gehalt_projekt_3_iv", target: "custom_gehalt_projekt_3", label: "Gehalt Projekt 3" },
                            { source: "custom_grundlage_der_vergütung_tarifdetails_pr_3_iv", target: "custom_grundlage_der_vergütung_tarifdetails_pr_3", label: "Vergütungsdetails Projekt 3" },
                            { source: "custom_ist_zusätzliche_vergütung_zum_grundgehalt_2_iv", target: "custom_ist_zusätzliche_vergütung_zum_grundgehalt_2", label: "Zusätzliche Vergütung" },
                            { source: "custom_jahressonderzahlung_projekt_3_in_p_iv", target: "custom_jahressonderzahlung_projekt_3", label: "Jahressonderzahlung Projekt 3" },
                            { source: "custom_jahressonderzahlung_projekt_3_in_e_iv", target: "custom_jahressonderzahlung_projekt_3_in_", label: "Sonderzahlung Projekt 3 in E" },
                        ],

                        custom_zulage_1_in_verhandlung: [
                            { source: "custom_projekt_zulage_1_iv", target: "custom_projekt_zulage_1", label: "Projekt Zulage 1" },
                            { source: "custom_kostenstelle_zulage_1_iv", target: "custom_kostenstelle_zulage_1", label: "Kostenstelle Zulage 1" },
                            { source: "custom_anfangsdatum_zulage_1_iv", target: "custom_anfangsdatum_zulage_1", label: "Anfangsdatum Zulage 1" },
                            { source: "custom_enddatum_zulage_1_iv", target: "custom_enddatum_zulage_1", label: "Enddatum Zulage 1" },
                            { source: "custom_zulage_zulage_1_iv", target: "custom_zulage_zulage_1", label: "Zulage Betrag 1" },
                            { source: "custom_positionsbezeichnung_zulage_1_iv", target: "custom_positionsbezeichnung_zulage_1", label: "Positionsbezeichnung Zulage 1" },
                            { source: "custom_wochenstunden_zulage_1_iv", target: "custom_wochenstunden_zulage_1", label: "Wochenstunden Zulage 1" }
                        ],
                        custom_zulage_2_in_verhandlung: [
                            { source: "custom_projekt_zulage_2_iv", target: "custom_projekt_zulage_2", label: "Projekt Zulage 2" },
                            { source: "custom_kostenstelle_zulage_2_iv", target: "custom_kostenstelle_zulage_2", label: "Kostenstelle Zulage 2" },
                            { source: "custom_anfangsdatum_zulage_2_iv", target: "custom_anfangsdatum_zulage_2", label: "Anfangsdatum Zulage 2" },
                            { source: "custom_enddatum_zulage_2_iv", target: "custom_enddatum_zulage_2", label: "Enddatum Zulage 2" },
                            { source: "custom_zulage_zulage_2_iv", target: "custom_zulage_zulage_2", label: "Zulage Zulage 2" },
                            { source: "custom_positionsbezeichnung_zulage_2_iv", target: "custom_positionsbezeichnung_zulage_2", label: "Positionsbezeichnung Zulage 2" },
                            { source: "custom_wochenstunden_zulage_2_iv", target: "custom_wochenstunden_zulage_2", label: "Wochenstunden Zulage 2" }
                        ]
                    };

                    // Änderungen anwenden
                    Object.keys(fields_map).forEach(parent_field => {
                        if (frm.doc[parent_field]) {
                            fields_map[parent_field].forEach(field => {
                                update_field(field.source, field.target, field.label);
                            });

                            // Check-Feld auf 0 setzen
                            frm.set_value(parent_field, 0);
                        }
                    });

                    // Speichern und Kommentar hinzufügen
                    frm.save().then(() => {
                        if (changes.length > 0) {
                            let table_html = `
                                <table style="width:100%; border-collapse: collapse;">
                                    <thead>
                                        <tr>
                                            <th>Geändertes Feld</th>
                                            <th>Alter Wert</th>
                                            <th>Neuer Wert</th>
                                            <th>Variablenname</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        ${changes.map(change => `
                                            <tr>
                                                <td>${change.field_name}</td>
                                                <td>${change.old_value}</td>
                                                <td>${change.new_value}</td>
                                                <td>${change.variable_name}</td>
                                            </tr>
                                        `).join('')}
                                    </tbody>
                                </table>
                            `;

                            frappe.call({
                                method: "frappe.client.insert",
                                args: {
                                    doc: {
                                        doctype: "Comment",
                                        comment_type: "Comment",
                                        reference_doctype: frm.doc.doctype,
                                        reference_name: frm.doc.name,
                                        content: table_html
                                    }
                                },
                                callback: function (r) {
                                    frappe.msgprint("Änderungen wurden erfolgreich protokolliert.");
                                }
                            });
                        } else {
                            frappe.msgprint("Keine Änderungen vorgenommen.");
                        }
                    }).catch(err => {
                        frappe.msgprint("Fehler beim Speichern der Änderungen.");
                        console.error(err);
                    });
                }
            );
        }, __('Aktionen'));
    }
});

frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        frm.add_custom_button(__('Vertragsverhandlungen übernehmen'), () => {
            frappe.confirm(
                __(
                    "Wollen Sie wirklich ALLE Vertragsverhandlungen in die aktuell laufenden Verträge übernehmen und dadurch die Daten der Vertragsverhandlung löschen? " +
                    "Alle Änderungen werden Sie in einem Protokoll in den Kommentaren finden. Der Vorgang ist unumkehrbar!"
                ),
                () => {
                    console.log("Confirmed: Starting Vertragsverhandlungen update");
                    frappe.msgprint("Vertragsverhandlungen werden bearbeitet...");

                    let changes = [];

                    function update_field(source, target, field_name) {
                        let source_value = frm.doc[source];

                        // Check if source_value is not null or undefined
                        if (source_value !== null && source_value !== undefined && source_value !== "") {
                            let original_value = frm.doc[target];

                            // Update target field
                            frm.set_value(target, source_value);

                            // Log the change
                            changes.push({
                                field_name: field_name,
                                old_value: original_value || 'leer',
                                new_value: source_value,
                                variable_name: target
                            });

                            // Clear source field
                            frm.set_value(source, null);
                        }
                    }

                    // Check and update fields for Grundvertrag
                    if (frm.doc.custom_grundvertrag_in_verhandlung) {
                        update_field("custom_wochenstunden_grundvertrag_in_verhandlung", "custom_wochenstunden_grundvertrag", "Wochenstunden Grundvertrag");
                        update_field("custom_gehalt_des_grundvertrags_in_verhandlung", "custom_gehalt_des_grundvertrags", "Gehalt des Grundvertrags");
                        update_field("custom_grundlage_der_vergütung_tarifdetails_in_verhandlung", "custom_grundlage_der_vergütung", "Grundlage der Vergütung");
                        update_field("custom_unterzeichnungsdatum_in_verhandlung", "custom_unterzeichnungsdatum", "Unterzeichnungsdatum");
                        update_field("custom_start_des_grundvertrags_in_verhandlung", "custom_start_des_grundvertrags", "Start des Grundvertrags");
                        update_field("custom_ende_des_arbeitsverhältnis_in_verhandlung", "custom_ende_des_arbeitsverhältnis", "Ende des Arbeitsverhältnisses");
                    }

                    // Clear source fields and reset negotiation flag for Grundvertrag
                    frm.set_value("custom_grundvertrag_in_verhandlung", 0);

                    // Speichern und Kommentar hinzufügen
                    frm.save().then(() => {
                        if (changes.length > 0) {
                            // Generate HTML Table for changes
                            let table_html = `
                                <table style="width:100%; border-collapse: collapse; margin-top: 10px;">
                                    <thead>
                                        <tr>
                                            <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Name Geändertes Feld</th>
                                            <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Alter Wert</th>
                                            <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Neuer Wert</th>
                                            <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Variablenname</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        ${changes.map(change => `
                                            <tr>
                                                <td style="border: 1px solid #ddd; padding: 8px;">${change.field_name}</td>
                                                <td style="border: 1px solid #ddd; padding: 8px;">${change.old_value}</td>
                                                <td style="border: 1px solid #ddd; padding: 8px;">${change.new_value}</td>
                                                <td style="border: 1px solid #ddd; padding: 8px;">${change.variable_name}</td>
                                            </tr>
                                        `).join('')}
                                    </tbody>
                                </table>
                            `;

                            console.log("Changes HTML:", table_html);

                            // Kommentar als HTML-Tabelle hinzufügen
                            frappe.call({
                                method: "frappe.client.insert",
                                args: {
                                    doc: {
                                        doctype: "Comment",
                                        comment_type: "Comment",
                                        reference_doctype: frm.doc.doctype,
                                        reference_name: frm.doc.name,
                                        content: table_html
                                    }
                                },
                                callback: function(response) {
                                    if (response.message) {
                                        frappe.msgprint({
                                            message: "Änderungen wurden erfolgreich als Kommentar hinzugefügt.",
                                            indicator: "green"
                                        });
                                    }
                                }
                            });
                        } else {
                            frappe.msgprint("Keine Änderungen vorgenommen.");
                        }

                        // Reload the document to reflect changes
                        frm.reload_doc();
                    }).catch(err => {
                        frappe.msgprint({
                            title: __('Fehler'),
                            message: __('Das Dokument konnte nicht gespeichert werden. Änderungen wurden nicht übernommen.'),
                            indicator: 'red'
                        });
                        console.error('Fehler beim Speichern:', err);
                    });
                }
            );
        }, __('Aktionen'));
    }
});

frappe.ui.form.on('Employee', {
    custom_grad_der_behinderung: function(frm) {
        // Prüfen, ob das Feld vorhanden ist
        if (!frm.doc.custom_grad_der_behinderung) {
            console.log("Feld 'custom_grad_der_behinderung' ist leer oder nicht vorhanden.");
            return;
        }

        let grad = frm.doc.custom_grad_der_behinderung;

        // Standardwerte setzen
        frm.set_value('custom_schwerbehinderung', 'Nein');
        frm.set_df_property('custom_schwerbehinderung', 'hidden', false);
        frm.set_df_property('custom_gleichstellung', 'hidden', true);

        if (grad === 'keine Behinderung') {
            // Wenn \"keine Behinderung\" ausgewählt ist, Feld ausblenden
            frm.set_df_property('custom_schwerbehinderung', 'hidden', true);
        } else if (['50%', '60%', '70%', '80%', '90%', '100%'].includes(grad)) {
            // Wenn Grad der Behinderung 50% oder mehr beträgt
            frm.set_value('custom_schwerbehinderung', 'Ja');
        } else if (['30%', '40%'].includes(grad)) {
            // Wenn Grad der Behinderung 30% oder 40% beträgt
            frm.set_df_property('custom_gleichstellung', 'hidden', false);
        }

        console.log(`Grad der Behinderung: ${grad}`);
    }
});

frappe.ui.form.on('Employee', {
    custom_projekt_1: function(frm) {
        check_single_projekt(frm, 'custom_projekt_1', 'custom_befristung_des_1_vertrags');
    },
    custom_projekt_2: function(frm) {
        check_single_projekt(frm, 'custom_projekt_2', 'custom_befristung_des_2_vertrags');
    },
    custom_projekt_3: function(frm) {
        check_single_projekt(frm, 'custom_projekt_3', 'custom_befristung_des_3_vertrags');
    },
    custom_projekt_4: function(frm) {
        check_single_projekt(frm, 'custom_projekt_4', 'custom_befristung_des_4_vertrags');
    }
});

// Funktion zur Überprüfung eines einzelnen Projektfeldes
function check_single_projekt(frm, projekt_feld, vertrags_feld) {
    var projekt = frm.doc[projekt_feld];

    if (!projekt) {
        // Setze die zugehörigen Felder zurück, wenn das Projektfeld geleert wird
        reset_fields(frm);
        return;
    }

    frappe.db.get_value('Project', projekt, [
        // 'custom_aufenthaltstitel_nötig',
        'custom_führungszeugnis_nötig',
        'custom_brandschutzhelfernachweis_benötigt',
        'custom_einverständniserklärung_akteneinsicht_notwendig',
        'custom_masernnachweis_nötig',
        'custom_gesundheitspass_nötig',
        'custom_ersthelfernachweis_benötigt',
        'custom_in_welchem_aktualisierungsturnus',
        'expected_end_date'
    ]).then(r => {
        if (r.message) {
            let data = r.message;

            frm.set_value(vertrags_feld, data.expected_end_date || '');

            // frm.set_value('custom_aufenthaltstitel_nötig', data.custom_aufenthaltstitel_nötig ? 1 : 0);
            frm.set_value('custom_führungszeugnis_nötig', data.custom_führungszeugnis_nötig ? 1 : 0);
            frm.set_value('custom_brandschutzhelfernachweis_benötigt', data.custom_brandschutzhelfernachweis_benötigt ? 1 : 0);
            frm.set_value('custom_einverständniserklärung_akteneinsicht_notwendig', data.custom_einverständniserklärung_akteneinsicht_notwendig ? 1 : 0);
            frm.set_value('custom_masernnachweis_nötig', data.custom_masernnachweis_nötig ? 1 : 0);
            frm.set_value('custom_gesundheitspass_nötig', data.custom_gesundheitspass_nötig ? 1 : 0);
            frm.set_value('custom_ersthelfernachweis_benötigt', data.custom_ersthelfernachweis_benötigt ? 1 : 0);
            frm.set_value('custom_in_welchem_aktualisierungsturnus', data.custom_in_welchem_aktualisierungsturnus || '');
        }
    });
}

// Funktion zum Zurücksetzen der Felder
function reset_fields(frm) {
    // frm.set_value('custom_aufenthaltstitel_nötig', 0);
    frm.set_value('custom_führungszeugnis_nötig', 0);
    frm.set_value('custom_brandschutzhelfernachweis_benötigt', 0);
    frm.set_value('custom_einverständniserklärung_akteneinsicht_notwendig', 0);
    frm.set_value('custom_masernnachweis_nötig', 0);
    frm.set_value('custom_gesundheitspass_nötig', 0);
    frm.set_value('custom_ersthelfernachweis_benötigt', 0);
    frm.set_value('custom_in_welchem_aktualisierungsturnus', '');
}

frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        // Führt die Überprüfung bei jedem Laden des Formulars aus
        check_projekte(frm);
    },
    custom_projekt_1: function(frm) {
        // Prüft das erste Projektfeld, wenn es geändert oder geleert wird
        check_projekte(frm);
    },
    custom_projekt_2: function(frm) {
        check_projekte(frm);
    },
    custom_projekt_3: function(frm) {
        check_projekte(frm);
    },
    custom_projekt_4: function(frm) {
        check_projekte(frm);
