// Funktion zur Überprüfung der Projekte und Aktualisierung der Felder im Mitarbeiter-Dokument
function check_projekte(frm) {
    // Liste der Projektfelder
    var projekt_felder = [
        frm.doc.custom_projekt_1,
        frm.doc.custom_projekt_2,
        frm.doc.custom_projekt_3,
        frm.doc.custom_projekt_4
    ];

    // Flags für die "Notwendigkeit"-Felder initialisieren
    var aufenthaltstitel_noetig = false;
    var fuehrungszeugnis_noetig = false;
    var brandschutzhelfernachweis_noetig = false;
    var einverstaendniserklaerung_noetig = false;
    var masernnachweis_noetig = false;
    var gesundheitspass_noetig = false;
    var ersthelfernachweis_noetig = false;
    var min_aktualisierungsturnus = null; // Für den niedrigsten Aktualisierungsturnus-Wert

    var count = 0;

    projekt_felder.forEach(function(projekt) {
        if (projekt) {
            // Datenbankabfrage für die Projektfelder
            frappe.db.get_value('Project', projekt, [
                'custom_aufenthaltstitel_nötig',
                'custom_führungszeugnis_nötig',
                'custom_brandschutzhelfernachweis_benötigt',
                'custom_einverständniserklärung_akteneinsicht_notwendig',
                'custom_masernnachweis_nötig',
                'custom_gesundheitspass_nötig',
                'custom_ersthelfernachweis_benötigt',
                'custom_in_welchem_aktualisierungsturnus',
                'expected_end_date'
            ], function(r) {
                if (r) {
                    // Notwendigkeit-Flags setzen, wenn in einem Projekt ein Feld auf 1 steht
                    aufenthaltstitel_noetig = aufenthaltstitel_noetig || r.custom_aufenthaltstitel_nötig;
                    fuehrungszeugnis_noetig = fuehrungszeugnis_noetig || r.custom_führungszeugnis_nötig;
                    brandschutzhelfernachweis_noetig = brandschutzhelfernachweis_noetig || r.custom_brandschutzhelfernachweis_benötigt;
                    einverstaendniserklaerung_noetig = einverstaendniserklaerung_noetig || r.custom_einverständniserklärung_akteneinsicht_notwendig;
                    masernnachweis_noetig = masernnachweis_noetig || r.custom_masernnachweis_nötig;
                    gesundheitspass_noetig = gesundheitspass_noetig || r.custom_gesundheitspass_nötig;
                    ersthelfernachweis_noetig = ersthelfernachweis_noetig || r.custom_ersthelfernachweis_benötigt;

                    // Niedrigsten Aktualisierungsturnus finden, falls gesetzt
                    if (r.custom_in_welchem_aktualisierungsturnus && (min_aktualisierungsturnus === null || r.custom_in_welchem_aktualisierungsturnus < min_aktualisierungsturnus)) {
                        min_aktualisierungsturnus = r.custom_in_welchem_aktualisierungsturnus;
                    }

                    // Übertragung des `expected_end_date` in das entsprechende Vertragsfeld
                    if (projekt === frm.doc.custom_projekt_1 && !frm.doc.custom_befristung_des_1_vertrags) {
                        frm.set_value('custom_befristung_des_1_vertrags', r.expected_end_date);
                    }
                    if (projekt === frm.doc.custom_projekt_2 && !frm.doc.custom_befristung_des_2_vertrags) {
                        frm.set_value('custom_befristung_des_2_vertrags', r.expected_end_date);
                    }
                    if (projekt === frm.doc.custom_projekt_3 && !frm.doc.custom_befristung_des_3_vertrags) {
                        frm.set_value('custom_befristung_des_3_vertrags', r.expected_end_date);
                    }
                    if (projekt === frm.doc.custom_projekt_4 && !frm.doc.custom_befristung_des_4_vertrags) {
                        frm.set_value('custom_befristung_des_4_vertrags', r.expected_end_date);
                    }
                }

                // Inkrementiere den Zähler und prüfe, ob alle Projekte verarbeitet wurden
                count++;
                if (count === projekt_felder.filter(p => p).length) {
                    // Alle Projekte wurden verarbeitet, also die Werte im Employee-Dokument setzen
                    frm.set_value('custom_aufenthaltstitel_nötig', aufenthaltstitel_noetig ? 1 : 0);
                    frm.set_value('custom_führungszeugnis_nötig', fuehrungszeugnis_noetig ? 1 : 0);
                    frm.set_value('custom_brandschutzhelfernachweis_benötigt', brandschutzhelfernachweis_noetig ? 1 : 0);
                    frm.set_value('custom_einverständniserklärung_akteneinsicht_notwendig', einverstaendniserklaerung_noetig ? 1 : 0);
                    frm.set_value('custom_masernnachweis_nötig', masernnachweis_noetig ? 1 : 0);
                    frm.set_value('custom_gesundheitspass_nötig', gesundheitspass_noetig ? 1 : 0);
                    frm.set_value('custom_ersthelfernachweis_benötigt', ersthelfernachweis_noetig ? 1 : 0);

                    // Aktualisierungsturnus setzen oder leeren
                    frm.set_value('custom_in_welchem_aktualisierungsturnus', min_aktualisierungsturnus !== null ? min_aktualisierungsturnus : '');
                }
            });
        } else {
            // Falls ein Projektfeld geleert wird, alle relevanten Felder auf 0 setzen und das Script erneut durchlaufen
            frm.set_value('custom_aufenthaltstitel_nötig', 0);
            frm.set_value('custom_führungszeugnis_nötig', 0);
            frm.set_value('custom_brandschutzhelfernachweis_benötigt', 0);
            frm.set_value('custom_einverständniserklärung_akteneinsicht_notwendig', 0);
            frm.set_value('custom_masernnachweis_nötig', 0);
            frm.set_value('custom_gesundheitspass_nötig', 0);
            frm.set_value('custom_ersthelfernachweis_benötigt', 0);
            frm.set_value('custom_in_welchem_aktualisierungsturnus', '');

            // Script erneut durchlaufen, um den aktuellen Stand zu prüfen
            check_projekte(frm);
        }
    });
}

