function set_earliest_contract_end(frm) {
    // Array der Felder, die geprüft werden sollen
    const dateFields = [
        { field: 'custom_ende_des_arbeitsverhältnis', label: 'Ende des Arbeitsverhältnisses' },
        { field: 'custom_befristung_des_1_vertrags', label: 'Befristung des 1. Vertrags' },
