        { field: 'custom_befristung_des_4_vertrags', label: 'Befristung des 4. Vertrags' },
        { field: 'custom_enddatum_zulage_1', label: 'Enddatum Zulage 1' },
        { field: 'custom_enddatum_zulage_2', label: 'Enddatum Zulage 2' }
    ];

    let earliestDate = null;
    let earliestLabel = '';

    // Durchlaufen der Felder und Ermittlung des frühesten Datums
    dateFields.forEach(dateField => {
        const fieldValue = frm.doc[dateField.field];
        if (fieldValue) {
            const fieldDate = new Date(fieldValue);
            if (!earliestDate || fieldDate < earliestDate) {
                earliestDate = fieldDate;
                earliestLabel = dateField.label;
            }
        }
    });

    // Festlegen des frühesten Datums und des Labels
    if (earliestDate) {
        frm.set_value('custom_nächstes_vertragsende', earliestDate.toISOString().split('T')[0]);
        frm.set_df_property('custom_nächstes_vertragsende', 'description', 'Nächstes Vertragsende: ' + earliestLabel);
    } else {
        // Leeren des Feldes und der Beschriftung, falls kein Datum vorhanden ist
        frm.set_value('custom_nächstes_vertragsende', '');
        frm.set_df_property('custom_nächstes_vertragsende', 'description', '');
    }
}

