// Funktion zur Überprüfung der Projekte
function check_projekte(frm) {
    var projekt_felder = [
        frm.doc.custom_projekt_1, 
        frm.doc.custom_projekt_2, 
        frm.doc.custom_projekt_3, 
        frm.doc.custom_projekt_4, 
        frm.doc.custom_projekt_5
    ];

    var min_aktualisierungsturnus = null;
    var aktualisierung_notwendig = false;

    // Überprüfe jedes Projektfeld
    for (var i = 0; i < projekt_felder.length; i++) {
        var projekt = projekt_felder[i];
        
        if (projekt) {
            // Datenbankabfrage für die Projektfelder durchführen
            frappe.db.get_value('Project', projekt, [
                'custom_aufenthaltstitel_nötig',
                'custom_führungszeugnis_nötig',
                'custom_brandschutzhelfernachweis_benötigt',
                'custom_einverständniserklärung_akteneinsicht_notwendig',
                'custom_masernnachweis_nötig',
                'custom_gesundheitspass_nötig',
                'custom_ersthelfernachweis_benötigt',
                'custom_in_welchem_aktualisierungsturnus'
            ], function(r) {
                if (r) {
                    // Setze oder entferne Werte für die jeweiligen Felder
                    frm.set_value('custom_aufenthaltstitel_nötig', r.custom_aufenthaltstitel_nötig || 0);
                    frm.set_value('custom_führungszeugnis_nötig', r.custom_führungszeugnis_nötig || 0);
                    frm.set_value('custom_brandschutzhelfernachweis_benötigt', r.custom_brandschutzhelfernachweis_benötigt || 0);
                    frm.set_value('custom_einverständniserklärung_akteneinsicht_notwendig', r.custom_einverständniserklärung_akteneinsicht_notwendig || 0);
                    frm.set_value('custom_masernnachweis_nötig', r.custom_masernnachweis_nötig || 0);
                    frm.set_value('custom_gesundheitspass_nötig', r.custom_gesundheitspass_nötig || 0);
                    frm.set_value('custom_ersthelfernachweis_benötigt', r.custom_ersthelfernachweis_benötigt || 0);

                    // Aktualisierungsturnus finden (niedrigsten Wert ermitteln)
                    if (r.custom_in_welchem_aktualisierungsturnus) {
                        var aktueller_turnus = r.custom_in_welchem_aktualisierungsturnus;
                        if (min_aktualisierungsturnus === null || aktueller_turnus < min_aktualisierungsturnus) {
                            min_aktualisierungsturnus = aktueller_turnus;
                        }
                    }
                    aktualisierung_notwendig = true;
                }
            });
        }
    }

    // Wenn keine Projektfelder gesetzt sind, setze die Zielfelder auf 0
    if (!aktualisierung_notwendig) {
        frm.set_value('custom_aufenthaltstitel_nötig', 0);
        frm.set_value('custom_führungszeugnis_nötig', 0);
        frm.set_value('custom_brandschutzhelfernachweis_benötigt', 0);
        frm.set_value('custom_einverständniserklärung_akteneinsicht_notwendig', 0);
        frm.set_value('custom_masernnachweis_nötig', 0);
        frm.set_value('custom_gesundheitspass_nötig', 0);
        frm.set_value('custom_ersthelfernachweis_benötigt', 0);
    }

    // Setze den niedrigsten Wert für custom_in_welchem_aktualisierungsturnus
    if (min_aktualisierungsturnus !== null) {
        frm.set_value('custom_in_welchem_aktualisierungsturnus', min_aktualisierungsturnus);
    } else {
        frm.set_value('custom_in_welchem_aktualisierungsturnus', '');
    }
}

