function berechne_neues_führungszeugnis(frm) {
    // Prüfen, ob das Führungszeugnis notwendig ist und ob beide Felder ausgefüllt sind
    if (frm.doc.custom_führungszeugnis_nötig && frm.doc.custom_datum_des_führungszeugnisses && frm.doc.custom_in_welchem_aktualisierungsturnus) {
        // Datum und Aktualisierungsturnus abrufen
        let datum_des_führungszeugnisses = frappe.datetime.str_to_obj(frm.doc.custom_datum_des_führungszeugnisses);
        let aktualisierungsturnus = parseInt(frm.doc.custom_in_welchem_aktualisierungsturnus);

        // Neues Datum berechnen
        datum_des_führungszeugnisses.setFullYear(datum_des_führungszeugnisses.getFullYear() + aktualisierungsturnus);
        let neues_datum = frappe.datetime.obj_to_str(datum_des_führungszeugnisses);

        // Berechnetes Datum in das Ziel-Feld eintragen
        frm.set_value('custom_neues_führungszeugnis_notwendig_am', neues_datum);
    } else {
        // Falls das Prüffeld deaktiviert ist oder eines der Felder leer ist, das Ziel-Feld leeren
        frm.set_value('custom_neues_führungszeugnis_notwendig_am', null);
    }
}

